actionConfiguration.body:
status => {
  if (status === 'BLACKLISTED') {
    return {
      status: 'BLACKLISTED',
      color: 'RGB(255, 0, 0)'
    };
  }
  ;
  if (status === 'REJECTED') {
    return {
      status: 'REJECTED',
      color: 'RGB(255, 0, 0)'
    };
  }
  if (status === 'PENDING') {
    return {
      status: 'PENDING',
      color: 'RGB(255, 165, 0)'
    };
  }
  return {
    status: 'VERIFIED',
    color: 'RGB(0, 128, 0)'
  };
}

