actionConfiguration.body:
action => {
  const currentChartIndex = appsmith.store.chartIndex || 0;
  if (action === 'RIGHT') {
    if (currentChartIndex === 1) {
      return;
    }
    storeValue('chartIndex', currentChartIndex + 1);
  } else {
    if (currentChartIndex === 0) {
      return;
    }
    storeValue('chartIndex', currentChartIndex - 1);
  }
}

