async () => {
  const data = await getAllKyc.run();
  const today = new Date();
  const last12Months = new Date(today.getFullYear(), today.getMonth() - 11, 1);
  const counts = {};
  data.filter(obj => obj.status === 'VERIFIED').forEach(obj => {
    const createdDate = new Date(obj.created);
    if (createdDate >= last12Months && createdDate <= today) {
      const monthYear = createdDate.toLocaleDateString('en-US', {
        month: 'short',
        year: 'numeric'
      });
      counts[monthYear] = (counts[monthYear] || 0) + 1;
    }
  });
  const result = [];
  let currentMonth = new Date(last12Months);
  while (currentMonth <= today) {
    const monthYear = currentMonth.toLocaleDateString('en-US', {
      month: 'short',
      year: 'numeric'
    });
    const count = counts[monthYear] || 0;
    result.push({
      monthYear,
      count
    });
    currentMonth.setMonth(currentMonth.getMonth() + 1);
  }
  return result;
}

